import React from "react";
import { motion } from "framer-motion";
import { FaEnvelope, FaPhone, FaMapMarkerAlt } from "react-icons/fa";

export default function About() {
  return (
    <div className="space-y-12">
      {/* Header */}
      <div>
        <motion.h1
          className="text-5xl font-bold"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          WELCOME TO...
        </motion.h1>
        <h2 className="text-3xl font-extrabold mt-4 text-purple-700">JACOB JONES</h2>
        <p className="italic text-gray-700">
          Product Designer based in <span className="font-bold">London</span>
        </p>
      </div>

      {/* Contact Info */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-gray-800">
        <div className="flex items-center gap-3">
          <FaPhone className="text-gray-600" /> +77 022 444 05 05
        </div>
        <div className="flex items-center gap-3">
          <FaMapMarkerAlt className="text-gray-600" /> United Kingdom, London
        </div>
        <div className="flex items-center gap-3">
          <FaEnvelope className="text-gray-600" /> jacob360@gmail.com
        </div>
      </div>

      {/* Experience Section */}
      <div className="bg-gradient-to-r from-purple-400 to-orange-400 text-white p-12 rounded-xl">
        <h3 className="text-lg italic">Experience</h3>
        <h2 className="text-4xl font-bold">MY EXPERIENCE</h2>
        <p className="mt-2 max-w-lg">
          Hello there! My name is Jacob Jones. I am a web designer & developer, and I'm very passionate and dedicated to my work.
        </p>
        <button className="mt-4 bg-black text-white px-6 py-2 rounded-lg">Download my resume</button>

        <div className="mt-8">
          {[
            { year: "2018 - Present", role: "LEAD UI/UX DESIGNER", company: "Rolling Thunder" },
            { year: "2018 - 2010", role: "SENIOR UI/UX DESIGNER", company: "Locost Accessories" },
            { year: "2008 - 2006", role: "JUNIOR UI/UX DESIGNER", company: "Sagebrush" }
          ].map(({ year, role, company }) => (
            <div key={role} className="mt-6">
              <p className="text-gray-200 text-sm">{year}</p>
              <h4 className="text-xl font-bold">{role}</h4>
              <p className="text-gray-300">{company}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
