import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, NavLink } from "react-router-dom";
import { FaBars, FaTimes, FaLinkedin, FaGithub, FaInstagram } from "react-icons/fa";
import Portfolio from "./Portfolio";
import About from "./About";
import Services from "./Services";
import Works from "./Works";
import Blog from "./Blog";
import Contact from "./Contact";

const navigationItems = [
  { name: "Home", path: "/", icon: <FaBars size={16} className="opacity-0" /> },
  { name: "About", path: "/about", icon: <FaBars size={16} className="opacity-0" /> },
  { name: "Services", path: "/services", icon: <FaBars size={16} className="opacity-0" /> },
  { name: "Works", path: "/works", icon: <FaBars size={16} className="opacity-0" /> },
  { name: "Blog", path: "/blog", icon: <FaBars size={16} className="opacity-0" /> },
  { name: "Contact", path: "/contact", icon: <FaBars size={16} className="opacity-0" /> },
];

const socialLinks = [
  { icon: <FaLinkedin size={22} />, href: "https://www.linkedin.com/in/imchiranjeevi", label: "LinkedIn" },
  { icon: <FaGithub size={22} />, href: "https://github.com/your-profile", label: "GitHub" }, // Update with your GitHub
  { icon: <FaInstagram size={22} />, href: "https://www.instagram.com/_imchiranjeevi", label: "Instagram" },
];

export default function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 text-gray-900 font-sans antialiased">
        {/* Navbar (Sidebar) */}
        <aside
          className={`fixed top-0 left-0 w-80 h-full bg-white text-gray-800 p-8 transform transition-all duration-500 ease-in-out z-50 shadow-2xl
            ${isMenuOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:static md:w-72 md:border-r md:border-gray-100`}
        >
          <div className="flex flex-col h-full">
            <div className="flex items-center justify-between mb-12">
              <h1 className="text-2xl font-bold text-gray-900 tracking-tight">Chiranjeevi Kumar</h1>
              <button className="md:hidden text-gray-500 hover:text-gray-700" onClick={toggleMenu}>
                <FaTimes size={24} />
              </button>
            </div>
            <nav className="flex-1 space-y-3">
              {navigationItems.map((item) => (
                <NavLink
                  key={item.name}
                  to={item.path}
                  end={item.path === "/"}
                  onClick={() => setIsMenuOpen(false)}
                  className={({ isActive }) =>
                    `group flex items-center gap-4 px-5 py-3 rounded-xl text-base font-medium transition-all duration-300 hover:bg-green-50 ${
                      isActive ? "bg-green-50 text-green-700 shadow-sm" : "text-gray-700"
                    }`
                  }
                >
                  <span
                    className={`w-1 h-6 rounded-full transition-all duration-300 ${
                      isActive ? "bg-green-500" : "bg-gray-300 group-hover:bg-green-400"
                    }`}
                  />
                  {item.name}
                </NavLink>
              ))}
            </nav>
            <div className="mt-auto pt-6 border-t border-gray-200">
              <div className="flex gap-6 mb-4">
                {socialLinks.map((link, index) => (
                  <a
                    key={index}
                    href={link.href}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-500 hover:text-green-600 transition-colors duration-300"
                    aria-label={link.label}
                  >
                    {link.icon}
                  </a>
                ))}
              </div>
              <p className="text-gray-500 text-sm">
                © {new Date().getFullYear()} Chiranjeevi Kumar
              </p>
            </div>
          </div>
        </aside>

        {/* Mobile Hamburger Button */}
        <button
          className="fixed top-6 left-6 z-50 md:hidden p-3 bg-white rounded-full shadow-lg text-gray-600 hover:bg-green-50 hover:text-green-700 transition-all duration-300"
          onClick={toggleMenu}
          aria-label="Toggle navigation"
        >
          {isMenuOpen ? <FaTimes size={22} /> : <FaBars size={22} />}
        </button>

        {/* Overlay for Mobile Menu */}
        {isMenuOpen && (
          <div
            className="fixed inset-0 bg-gray-900 bg-opacity-60 z-40 md:hidden transition-opacity duration-300"
            onClick={toggleMenu}
          />
        )}

        {/* Main Content */}
        <main className="flex-1 md:ml-72 p-6 sm:p-8 md:p-12 lg:p-16 xl:p-20 overflow-y-auto bg-white shadow-inner">
          <div className="max-w-7xl mx-auto">
            <Routes>
              <Route path="/" element={<Portfolio />} />
              <Route path="/about" element={<About />} />
              <Route path="/services" element={<Services />} />
              <Route path="/works" element={<Works />} />
              <Route path="/blog" element={<Blog />} />
              <Route path="/contact" element={<Contact />} />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
}
