import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPaperclip, FaPhone, FaEnvelope, FaMapMarkerAlt, FaLinkedin, FaGithub } from "react-icons/fa";
import emailjs from "@emailjs/browser";

const contactInfo = {
  phone: "+91 7870503085",
  email: "chiranjeeviraj109@gmail.com",
  location: "India",
  socials: [
    { name: "LinkedIn", href: "https://linkedin.com/in/imchiranjeevi", icon: <FaLinkedin size={24} /> },
    { name: "GitHub", href: "https://github.com/Chiranjeeviprogrammer", icon: <FaGithub size={24} /> },
  ],
};

// Animation Variants
const fadeInUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
};

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    file: null,
  });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus("sending");

    const templateParams = {
      from_name: formData.name,
      from_email: formData.email,
      message: formData.message,
      reply_to: formData.email,
    };

    emailjs
      .send(
        "YOUR_SERVICE_ID", // Replace with your EmailJS Service ID
        "YOUR_TEMPLATE_ID", // Replace with your EmailJS Template ID
        templateParams,
        "YOUR_PUBLIC_KEY" // Replace with your EmailJS Public Key
      )
      .then(
        (result) => {
          setStatus("success");
          setFormData({ name: "", email: "", message: "", file: null });
          setTimeout(() => setStatus(""), 3000);
        },
        (error) => {
          console.error("EmailJS Error:", error);
          setStatus("error");
          setTimeout(() => setStatus(""), 3000);
        }
      );
  };

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-50 to-white py-16 md:py-24 flex items-center">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 grid grid-cols-1 lg:grid-cols-2 gap-12">
        {/* Left Section: Contact Info */}
        <motion.div
          className="bg-gradient-to-br from-gray-900 to-gray-800 text-white p-8 rounded-3xl shadow-2xl flex flex-col justify-between"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <div>
            <motion.h1
              className="text-4xl md:text-5xl font-bold tracking-tight mb-4"
              variants={fadeInUp}
            >
              Let’s Connect
            </motion.h1>
            <motion.p
              className="text-gray-300 text-base md:text-lg leading-relaxed"
              variants={fadeInUp}
            >
              Have a project in mind or just want to chat? Reach out!
            </motion.p>
            <motion.div className="mt-10 space-y-6" variants={staggerContainer}>
              {[
                { icon: <FaMapMarkerAlt className="text-green-400" size={22} />, text: contactInfo.location },
                { icon: <FaPhone className="text-green-400" size={22} />, text: contactInfo.phone, href: `tel:${contactInfo.phone}` },
                { icon: <FaEnvelope className="text-green-400" size={22} />, text: contactInfo.email, href: `mailto:${contactInfo.email}` },
              ].map((item, index) => (
                <motion.div key={index} className="flex items-center gap-4" variants={fadeInUp}>
                  {item.icon}
                  {item.href ? (
                    <a href={item.href} className="text-gray-200 hover:text-green-400 transition-colors duration-300">
                      {item.text}
                    </a>
                  ) : (
                    <span className="text-gray-200">{item.text}</span>
                  )}
                </motion.div>
              ))}
            </motion.div>
          </div>
          <motion.div className="mt-12 flex gap-6" variants={fadeInUp}>
            {contactInfo.socials.map((social, index) => (
              <motion.a
                key={index}
                href={social.href}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-300 hover:text-green-400 transition-all duration-300"
                whileHover={{ scale: 1.15, rotate: 5 }}
                aria-label={social.name}
              >
                {social.icon}
              </motion.a>
            ))}
          </motion.div>
        </motion.div>

        {/* Right Section: Contact Form */}
        <motion.div
          className="bg-white p-8 rounded-3xl shadow-2xl border border-gray-100"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <motion.h2
            className="text-3xl md:text-4xl font-semibold text-gray-900 mb-6 tracking-tight"
            variants={fadeInUp}
          >
            Drop Me a Line
          </motion.h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            {[
              { type: "text", name: "name", placeholder: "Your Name" },
              { type: "email", name: "email", placeholder: "Your Email" },
            ].map((input, index) => (
              <motion.input
                key={index}
                type={input.type}
                name={input.name}
                placeholder={input.placeholder}
                value={formData[input.name]}
                onChange={handleChange}
                className="w-full px-5 py-3 bg-gray-50 border border-gray-200 rounded-xl text-gray-700 focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition-all duration-300 placeholder-gray-400"
                required
                variants={fadeInUp}
                whileFocus={{ scale: 1.02, boxShadow: "0 6px 12px rgba(0, 0, 0, 0.05)" }}
              />
            ))}
            <motion.textarea
              name="message"
              placeholder="Your Message"
              rows="5"
              value={formData.message}
              onChange={handleChange}
              className="w-full px-5 py-3 bg-gray-50 border border-gray-200 rounded-xl text-gray-700 focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition-all duration-300 resize-y placeholder-gray-400"
              required
              variants={fadeInUp}
              whileFocus={{ scale: 1.02, boxShadow: "0 6px 12px rgba(0, 0, 0, 0.05)" }}
            />
            <motion.label
              className="flex items-center gap-3 text-gray-600 hover:text-green-600 transition-colors duration-300 cursor-pointer group"
              variants={fadeInUp}
            >
              <FaPaperclip size={20} className="group-hover:rotate-45 transition-transform duration-300" />
              <span className="text-sm font-medium">
                {formData.file ? `Attached: ${formData.file.name}` : "Attach a File (optional)"}
              </span>
              <input
                type="file"
                name="file"
                onChange={handleChange}
                className="hidden"
              />
            </motion.label>
            <motion.button
              type="submit"
              disabled={status === "sending"}
              className={`w-full px-6 py-3 rounded-xl font-semibold text-white shadow-lg transition-all duration-300 ${
                status === "sending"
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-gradient-to-r from-green-600 to-teal-600 hover:from-green-700 hover:to-teal-700"
              }`}
              variants={fadeInUp}
              whileHover={{ scale: status === "sending" ? 1 : 1.05 }}
              whileTap={{ scale: status === "sending" ? 1 : 0.95 }}
            >
              {status === "sending" ? "Sending..." : "Send Message"}
            </motion.button>
          </form>
          {status === "success" && (
            <motion.p
              className="mt-4 text-green-600 text-center font-medium bg-green-50 py-2 rounded-lg"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
            >
              Message sent successfully!
            </motion.p>
          )}
          {status === "error" && (
            <motion.p
              className="mt-4 text-red-600 text-center font-medium bg-red-50 py-2 rounded-lg"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
            >
              Failed to send message. Please try again.
            </motion.p>
          )}
        </motion.div>
      </div>
    </section>
  );
}
