import React from "react";
import { motion } from "framer-motion";
import { FaBehance, FaDribbble, FaInstagram } from "react-icons/fa";

export default function Portfolio() {
  return (
    <div className="flex min-h-screen bg-gray-900 text-white">
      {/* Sidebar */}
      <aside className="w-1/4 p-8 bg-black flex flex-col justify-between">
        <div>
          <h1 className="text-3xl font-bold">Jac.</h1>
          <nav className="mt-6">
            {['Home', 'About', 'Services', 'Works', 'Blogs', 'Contact'].map((item) => (
              <p key={item} className="text-gray-400 hover:text-white cursor-pointer mb-4">{item}</p>
            ))}
          </nav>
        </div>
        <div className="flex gap-4">
          <FaBehance size={24} />
          <FaDribbble size={24} />
          <FaInstagram size={24} />
        </div>
        <p className="text-gray-500 text-sm">Copyright Â©2023 Jacob Jones. All rights reserved.</p>
      </aside>

      {/* Main Content */}
      <main className="w-3/4 p-16 flex items-center justify-between">
        <div>
          <motion.h1
            className="text-6xl font-bold mb-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
          >
            My Name <br /> is Jacob Jones...
          </motion.h1>
          <p className="text-xl italic text-gray-300 mb-6">Product Designer based in London</p>
          <button className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-lg">
            Letâ€™s talk with me
          </button>
          <div className="mt-4 flex gap-4 items-center">
            <p>ðŸ“ž +77 022 444 05 05</p>
            <p>ðŸ“§ jacob360@gmail.com</p>
          </div>
        </div>

        {/* Profile Image Section */}
        <motion.div
          className="relative w-1/3"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <img src="/profile-image.jpg" alt="Jacob Jones" className="rounded-full" />
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500 to-orange-500 opacity-50 rounded-full"></div>
        </motion.div>
      </main>
    </div>
  );
}
