import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPlus, FaMinus } from "react-icons/fa";

const services = [
  { title: "Web Design", description: "Customize a template or create from scratch with an immersive library.", image: null },
  { title: "UI/UX Design", description: "Enhance user experience with well-researched UI/UX principles.", image: null },
  { title: "Mobile Application", description: "Build high-performance mobile applications with modern frameworks.", image: "/your-image-path.jpg" },
  { title: "User Research", description: "Conduct user research to improve product usability and design.", image: null }
];

export default function Services() {
  const [expanded, setExpanded] = useState(null);

  const toggleExpand = (index) => {
    setExpanded(expanded === index ? null : index);
  };

  return (
    <div className="p-8 md:p-16">
      <h2 className="text-gray-500 italic">Service</h2>
      <h1 className="text-4xl md:text-6xl font-bold mb-8">MY SPECIALTIES</h1>

      {services.map((service, index) => (
        <div key={index} className="border-b py-4">
          <div className="flex justify-between items-center cursor-pointer" onClick={() => toggleExpand(index)}>
            <h3 className={`text-xl md:text-2xl font-semibold ${service.image ? "text-purple-500" : ""}`}>
              â€¢ {service.title}
            </h3>
            {expanded === index ? <FaMinus /> : <FaPlus />}
          </div>
          {expanded === index && (
            <motion.div
              className="mt-4 text-gray-600 flex flex-col md:flex-row items-start md:items-center gap-6"
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: "auto" }}
              transition={{ duration: 0.5 }}
            >
              {service.image && <img src={service.image} alt={service.title} className="w-32 md:w-48 rounded-lg shadow-lg" />}
              <p>{service.description}</p>
            </motion.div>
          )}
        </div>
      ))}
    </div>
  );
}
