import React from "react";

const blogPosts = [
  {
    id: 1,
    image: "https://source.unsplash.com/400x300/?laptop,workspace",
    date: "10 July, 2022",
    title: "12 unique examples of portfolio websites",
  },
  {
    id: 2,
    image: "https://source.unsplash.com/400x300/?business,meeting",
    date: "10 July, 2022",
    title: "12 unique examples of portfolio websites",
  },
  {
    id: 3,
    image: "https://source.unsplash.com/400x300/?technology,phone",
    date: "10 July, 2022",
    title: "12 unique examples of portfolio websites",
  },
];

export default function blog() {
  return (
    <div className="p-8">
      <h2 className="text-center text-lg italic text-gray-500">Blog</h2>
      <h1 className="text-center text-4xl font-bold mt-2">READ MY BLOG</h1>

      {/* Blog Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mt-8">
        {blogPosts.map((post) => (
          <div key={post.id} className="shadow-lg p-4 rounded-lg">
            <img src={post.image} alt="Blog" className="w-full h-48 object-cover rounded-lg" />
            <p className="text-gray-500 italic mt-4">{post.date}</p>
            <h3 className="font-bold text-lg mt-2">{post.title}</h3>
          </div>
        ))}
      </div>

      {/* Load More Button */}
      <div className="flex justify-center mt-8">
        <button className="px-6 py-3 bg-black text-white rounded-lg hover:bg-gray-800 flex items-center gap-2">
          Load more
          <span role="img" aria-label="arrow">↗️</span>
        </button>
      </div>
    </div>
  );
}
