import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPaperclip, FaPhone, FaEnvelope, FaMapMarkerAlt, FaLinkedin, FaGithub } from "react-icons/fa";
import emailjs from "@emailjs/browser";

const contactInfo = {
  phone: "+91 7870503085",
  email: "chiranjeeviraj109@gmail.com",
  location: "India",
  socials: [
    { name: "LinkedIn", href: "https://linkedin.com/in/imchiranjeevi", icon: <FaLinkedin size={24} /> },
    { name: "GitHub", href: "https://github.com/Chiranjeeviprogrammer", icon: <FaGithub size={24} /> },
  ],
};

const fadeInUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

export default function Contact() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "", file: null });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({ ...prev, [name]: files ? files[0] : value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus("sending");

    emailjs.send("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", {
      from_name: formData.name,
      from_email: formData.email,
      message: formData.message,
      reply_to: formData.email,
    }, "YOUR_PUBLIC_KEY").then(
      () => {
        setStatus("success");
        setFormData({ name: "", email: "", message: "", file: null });
        setTimeout(() => setStatus(""), 3000);
      },
      () => setStatus("error")
    );
  };

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-16 flex items-center">
      <div className="max-w-6xl mx-auto px-6 grid grid-cols-1 lg:grid-cols-2 gap-12">
        {/* Left Section */}
        <motion.div className="bg-gradient-to-br from-gray-900 to-gray-800 text-white p-8 rounded-3xl shadow-lg flex flex-col justify-between">
          <motion.h1 className="text-4xl font-bold mb-4" variants={fadeInUp}>Letâ€™s Connect</motion.h1>
          <motion.p className="text-gray-300 mb-6" variants={fadeInUp}>Have a project in mind or just want to chat? Reach out!</motion.p>
          <motion.div className="space-y-5">
            {[{ icon: <FaMapMarkerAlt className="text-green-400" />, text: contactInfo.location },
              { icon: <FaPhone className="text-green-400" />, text: contactInfo.phone, href: `tel:${contactInfo.phone}` },
              { icon: <FaEnvelope className="text-green-400" />, text: contactInfo.email, href: `mailto:${contactInfo.email}` }].map((item, i) => (
              <motion.div key={i} className="flex items-center gap-4" variants={fadeInUp}>
                {item.icon}
                {item.href ? <a href={item.href} className="hover:text-green-400">{item.text}</a> : <span>{item.text}</span>}
              </motion.div>
            ))}
          </motion.div>
          <motion.div className="flex gap-6 mt-6">
            {contactInfo.socials.map((social, i) => (
              <motion.a key={i} href={social.href} target="_blank" className="text-gray-300 hover:text-green-400">
                {social.icon}
              </motion.a>
            ))}
          </motion.div>
        </motion.div>

        {/* Right Section */}
        <motion.div className="bg-white p-8 rounded-3xl shadow-lg border border-gray-200">
          <motion.h2 className="text-3xl font-semibold text-gray-900 mb-6" variants={fadeInUp}>Drop Me a Line</motion.h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            {[{ type: "text", name: "name", placeholder: "Your Name" }, { type: "email", name: "email", placeholder: "Your Email" }].map((input, i) => (
              <motion.input key={i} type={input.type} name={input.name} placeholder={input.placeholder} 
                className="w-full px-5 py-3 border rounded-xl focus:ring-2 focus:ring-green-500" required variants={fadeInUp} />
            ))}
            <motion.textarea name="message" placeholder="Your Message" rows="5" 
              className="w-full px-5 py-3 border rounded-xl focus:ring-2 focus:ring-green-500" required variants={fadeInUp} />
            <motion.button type="submit" disabled={status === "sending"} 
              className={`w-full px-6 py-3 rounded-xl text-white shadow-lg transition-all duration-300 ${
                status === "sending" ? "bg-gray-400" : "bg-green-600 hover:bg-green-700"
              }`} variants={fadeInUp}>
              {status === "sending" ? "Sending..." : "Send Message"}
            </motion.button>
          </form>
          {status === "success" && <motion.p className="mt-4 text-green-600">Message sent successfully!</motion.p>}
          {status === "error" && <motion.p className="mt-4 text-red-600">Failed to send message. Please try again.</motion.p>}
        </motion.div>
      </div>
    </section>
  );
}
