import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPaperclip, FaPhone, FaEnvelope, FaMapMarkerAlt, FaLinkedin, FaGithub } from "react-icons/fa";

const contactInfo = {
  phone: "+91 7870503085",
  email: "chiranjeeviraj109@gmail.com",
  location: "India",
  socials: [
    { name: "LinkedIn", href: "https://linkedin.com/in/your-profile", icon: <FaLinkedin size={24} /> },
    { name: "GitHub", href: "https://github.com/your-profile", icon: <FaGithub size={24} /> },
  ],
};

// Animation Variants
const fadeInUp = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
};

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    file: null,
  });
  const [status, setStatus] = useState(""); // Added for submission feedback

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus("sending"); // Indicate submission in progress

    // Simulate form submission (replace with EmailJS if desired)
    setTimeout(() => {
      console.log("Form Submitted:", formData);
      setStatus("success");
      setFormData({ name: "", email: "", message: "", file: null });

      // Reset status after 3 seconds
      setTimeout(() => setStatus(""), 3000);
    }, 1000);

    // Optional: EmailJS integration
    /*
    import emailjs from "emailjs-com";
    emailjs.send("your_service_id", "your_template_id", formData, "your_user_id")
      .then(() => {
        setStatus("success");
        setFormData({ name: "", email: "", message: "", file: null });
        setTimeout(() => setStatus(""), 3000);
      })
      .catch((error) => {
        console.error("Error:", error);
        setStatus("error");
        setTimeout(() => setStatus(""), 3000);
      });
    */
  };

  return (
    <section className="min-h-screen bg-gray-50 text-gray-900 px-4 sm:px-6 lg:px-8 py-12 md:py-20 flex items-center">
      <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Left Section: Contact Info */}
        <motion.div
          className="bg-gray-900 text-white p-8 rounded-xl shadow-lg flex flex-col justify-center"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <motion.span className="text-sm text-gray-400 uppercase tracking-wide" variants={fadeInUp}>
            Contact
          </motion.span>
          <motion.h1
            className="text-3xl md:text-4xl font-bold text-white mt-2"
            variants={fadeInUp}
          >
            Reach Out to Me
          </motion.h1>
          <motion.div className="mt-6 space-y-4" variants={staggerContainer}>
            <motion.p className="flex items-center gap-3 text-gray-300" variants={fadeInUp}>
              <FaMapMarkerAlt className="text-green-500" /> {contactInfo.location}
            </motion.p>
            <motion.p
              className="flex items-center gap-3 text-lg font-semibold text-gray-100 hover:text-green-400 transition-colors"
              variants={fadeInUp}
            >
              <FaPhone className="text-green-500" />
              <a href={`tel:${contactInfo.phone}`}>{contactInfo.phone}</a>
            </motion.p>
            <motion.p
              className="flex items-center gap-3 text-lg font-semibold text-gray-100 hover:text-green-400 transition-colors"
              variants={fadeInUp}
            >
              <FaEnvelope className="text-green-500" />
              <a href={`mailto:${contactInfo.email}`}>{contactInfo.email}</a>
            </motion.p>
          </motion.div>
          <motion.div className="mt-8 flex gap-6" variants={fadeInUp}>
            {contactInfo.socials.map((social, index) => (
              <a
                key={index}
                href={social.href}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-400 hover:text-green-500 transition-colors duration-200"
                aria-label={social.name}
              >
                {social.icon}
              </a>
            ))}
          </motion.div>
        </motion.div>

        {/* Right Section: Contact Form */}
        <motion.div
          className="bg-white p-8 rounded-xl shadow-lg flex flex-col justify-center"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <motion.h2
            className="text-2xl md:text-3xl font-semibold text-gray-900 mb-6"
            variants={fadeInUp}
          >
            Have a Project?
          </motion.h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 gap-6">
              <motion.input
                type="text"
                name="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-gray-100 rounded-md text-gray-700 focus:ring-2 focus:ring-green-500 focus:outline-none transition-all duration-200"
                required
                variants={fadeInUp}
                whileFocus={{ scale: 1.02 }}
              />
              <motion.input
                type="email"
                name="email"
                placeholder="Your Email"
                value={formData.email}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-gray-100 rounded-md text-gray-700 focus:ring-2 focus:ring-green-500 focus:outline-none transition-all duration-200"
                required
                variants={fadeInUp}
                whileFocus={{ scale: 1.02 }}
              />
            </div>
            <motion.textarea
              name="message"
              placeholder="Your Message"
              rows="4"
              value={formData.message}
              onChange={handleChange}
              className="w-full px-4 py-3 bg-gray-100 rounded-md text-gray-700 focus:ring-2 focus:ring-green-500 focus:outline-none transition-all duration-200 resize-none"
              required
              variants={fadeInUp}
              whileFocus={{ scale: 1.02 }}
            />
            <motion.label
              className="flex items-center gap-2 text-gray-600 cursor-pointer hover:text-green-600 transition-colors duration-200"
              variants={fadeInUp}
            >
              <FaPaperclip />
              <span>Attach File {formData.file ? `(${formData.file.name})` : ""}</span>
              <input
                type="file"
                name="file"
                onChange={handleChange}
                className="hidden"
              />
            </motion.label>
            <motion.button
              type="submit"
              disabled={status === "sending"}
              className={`w-full px-6 py-3 rounded-md font-medium flex items-center justify-center gap-2 transition-colors duration-200 ${
                status === "sending"
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-green-600 text-white hover:bg-green-700"
              }`}
              variants={fadeInUp}
              whileHover={{ scale: status === "sending" ? 1 : 1.05 }}
              whileTap={{ scale: status === "sending" ? 1 : 0.95 }}
            >
              {status === "sending" ? "Sending..." : "Submit Now"} <span>➡️</span>
            </motion.button>
          </form>
          {status === "success" && (
            <motion.p
              className="mt-4 text-green-600 text-center"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              Message sent successfully!
            </motion.p>
          )}
          {status === "error" && (
            <motion.p
              className="mt-4 text-red-600 text-center"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              Failed to send message. Please try again.
            </motion.p>
          )}
        </motion.div>
      </div>
    </section>
  );
}
