import React from "react";
import { FaBehance, FaDribbble, FaInstagram } from "react-icons/fa";
import { motion } from "framer-motion";

export default function Portfolio() {
  return (
    <div className="flex min-h-screen bg-white text-black">
      {/* Sidebar */}
      <aside className="w-1/4 p-8 bg-black text-white flex flex-col justify-between">
        <div>
          <h1 className="text-3xl font-bold">Jac.</h1>
          <nav className="mt-6">
            {["Home", "About", "Services", "Works", "Blogs", "Contact"].map((item, index) => (
              <p key={index} className={`mb-4 cursor-pointer text-gray-400 hover:text-white ${index === 0 ? "font-bold border-l-4 border-orange-500 pl-2" : ""}`}>{item}</p>
            ))}
          </nav>
        </div>
        <div className="flex gap-4">
          <FaBehance size={24} />
          <FaDribbble size={24} />
          <FaInstagram size={24} />
        </div>
        <p className="text-gray-500 text-sm">Copyright &copy; 2023 Jacob Jones. All rights reserved.</p>
      </aside>

      {/* Main Content */}
      <main className="w-3/4 p-16 flex items-center justify-between">
        <div>
          <motion.h1
            className="text-6xl font-bold mb-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
          >
            My Name <br /> is <span className="text-orange-500">Jacob</span> Jones...
          </motion.h1>
          <p className="text-xl italic text-gray-600 mb-6">Product Designer based in <span className="font-bold">London</span></p>
          <button className="bg-black text-white px-6 py-2 rounded-lg flex items-center gap-2">
            Letâ€™s talk with me
            <span className="text-orange-500">â†—</span>
          </button>
          <div className="mt-4 flex gap-6 items-center">
            <p className="font-bold">ðŸ“ž +77 022 444 05 05</p>
            <p className="font-bold">ðŸ“§ jacob360@gmail.com</p>
          </div>
        </div>

        {/* Profile Image Section */}
        <motion.div
          className="relative w-1/3"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <img src="/01_Home.jpg" alt="Jacob Jones" className="rounded-full shadow-lg" />
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500 to-orange-500 opacity-50 rounded-full"></div>
        </motion.div>
      </main>
    </div>
  );
}
