import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FaArrowRight, FaArrowLeft } from "react-icons/fa";

// Animation Variants
const fadeInUp = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
  exit: { opacity: 0, y: -20, transition: { duration: 0.4 } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2, delayChildren: 0.1 } },
};

// Data (unchanged)
const PROJECTS = [
  { id: 1, title: "EV Charging App", category: "Mobile App", image: "/your-image-path1.jpg", description: "A mobile solution for EV charging" },
  { id: 2, title: "Fintech Wallet", category: "Mobile App", image: "/your-image-path2.jpg", description: "Secure digital wallet solution" },
  { id: 3, title: "E-commerce App", category: "Mobile App", image: "/your-image-path3.jpg", description: "Online shopping platform" },
  { id: 4, title: "Bally Website Research", category: "UX Case Study", image: "/your-image-path4.jpg", description: "UX research project" },
];

const TESTIMONIALS = [
  {
    id: 1,
    quote: "File storage made easy – including powerful features you won’t find anywhere else.",
    author: "Larry Diamond",
    position: "Chief Executive Officer, Besnik",
    image: "/your-testimonial-image.jpg",
  },
  {
    id: 2,
    quote: "Great design work! It has helped improve our brand and user experience significantly.",
    author: "Jessica Smith",
    position: "Product Manager, TechCorp",
    image: "/your-testimonial-image2.jpg",
  },
];

// Project Card Component
const ProjectCard = ({ project }) => (
  <motion.div
    className="flex flex-col group cursor-pointer bg-white rounded-xl shadow-md overflow-hidden"
    variants={fadeInUp}
    whileHover={{ scale: 1.03, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }}
    transition={{ duration: 0.3 }}
  >
    <div className="relative overflow-hidden">
      <img
        src={project.image}
        alt={project.title}
        className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-110"
      />
      <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-opacity duration-300" />
    </div>
    <div className="p-6">
      <p className="text-gray-600 text-sm uppercase tracking-wide font-medium">{project.category}</p>
      <h3 className="text-xl font-semibold mt-2 text-gray-900 group-hover:text-green-600 transition-colors">
        {project.title}
      </h3>
      <p className="text-gray-500 mt-2 line-clamp-2">{project.description}</p>
      <span className="inline-flex items-center gap-2 mt-4 text-green-600 opacity-0 group-hover:opacity-100 transition-opacity">
        Learn More <FaArrowRight />
      </span>
    </div>
  </motion.div>
);

// Testimonial Card Component
const TestimonialCard = ({ testimonial, onPrev, onNext }) => (
  <motion.div
    className="mt-12 p-8 rounded-xl bg-gradient-to-r from-green-600 to-teal-600 text-white shadow-2xl overflow-hidden relative"
    variants={fadeInUp}
  >
    <div className="flex flex-col md:flex-row items-center gap-8">
      <motion.img
        src={testimonial.image}
        alt={testimonial.author}
        className="w-32 h-32 md:w-40 md:h-40 rounded-full shadow-lg object-cover border-4 border-white/20"
        whileHover={{ rotate: 5 }}
      />
      <div className="flex-1">
        <span className="text-sm uppercase tracking-wide opacity-80 font-medium">Testimonial</span>
        <h2 className="text-2xl md:text-4xl font-bold mt-1 mb-4">Client Feedback</h2>
        <blockquote className="text-lg italic leading-relaxed">“{testimonial.quote}”</blockquote>
        <div className="mt-6">
          <p className="font-semibold text-lg">{testimonial.author}</p>
          <p className="text-sm opacity-80">{testimonial.position}</p>
        </div>
      </div>
    </div>
    <div className="absolute bottom-6 right-6 flex gap-3">
      <button
        onClick={onPrev}
        className="p-3 bg-white/20 hover:bg-white/30 rounded-full transition-all duration-300 hover:scale-110"
        aria-label="Previous testimonial"
      >
        <FaArrowLeft />
      </button>
      <button
        onClick={onNext}
        className="p-3 bg-white/20 hover:bg-white/30 rounded-full transition-all duration-300 hover:scale-110"
        aria-label="Next testimonial"
      >
        <FaArrowRight />
      </button>
    </div>
  </motion.div>
);

export default function Works() {
  const [visibleProjects, setVisibleProjects] = useState(4);
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  // Auto-play testimonials
  useEffect(() => {
    if (isAutoPlaying) {
      const timer = setInterval(() => {
        setCurrentTestimonial((prev) => (prev + 1) % TESTIMONIALS.length);
      }, 5000);
      return () => clearInterval(timer);
    }
  }, [isAutoPlaying]);

  const handleLoadMore = () => setVisibleProjects((prev) => Math.min(prev + 2, PROJECTS.length));
  const handleNextTestimonial = () => {
    setIsAutoPlaying(false);
    setCurrentTestimonial((prev) => (prev + 1) % TESTIMONIALS.length);
  };
  const handlePrevTestimonial = () => {
    setIsAutoPlaying(false);
    setCurrentTestimonial((prev) => (prev - 1 + TESTIMONIALS.length) % TESTIMONIALS.length);
  };

  return (
    <section className="py-16 md:py-24 px-4 sm:px-6 lg:px-8 bg-gradient-to-b from-gray-50 to-white">
      <div className="max-w-7xl mx-auto">
        <motion.header
          className="mb-16 text-center"
          variants={staggerContainer}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          <motion.span className="text-green-600 text-sm uppercase tracking-widest font-medium" variants={fadeInUp}>
            Our Work
          </motion.span>
          <motion.h1
            className="text-4xl md:text-5xl font-bold mt-2 text-gray-900 tracking-tight"
            variants={fadeInUp}
          >
            Recent Projects
          </motion.h1>
          <motion.p className="mt-4 text-gray-600 max-w-2xl mx-auto" variants={fadeInUp}>
            Explore our latest works showcasing innovative solutions and exceptional design.
          </motion.p>
        </motion.header>

        <motion.div
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-16"
          variants={staggerContainer}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {PROJECTS.slice(0, visibleProjects).map((project) => (
            <ProjectCard key={project.id} project={project} />
          ))}
        </motion.div>

        {visibleProjects < PROJECTS.length && (
          <motion.button
            onClick={handleLoadMore}
            className="mx-auto flex items-center gap-3 px-8 py-3 bg-green-600 text-white font-medium rounded-full hover:bg-green-700 transition-all duration-300 hover:shadow-lg"
            variants={fadeInUp}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Load More <FaArrowRight />
          </motion.button>
        )}

        <AnimatePresence mode="wait">
          <motion.div
            key={currentTestimonial}
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={fadeInUp}
          >
            <TestimonialCard
              testimonial={TESTIMONIALS[currentTestimonial]}
              onPrev={handlePrevTestimonial}
              onNext={handleNextTestimonial}
            />
          </motion.div>
        </AnimatePresence>
      </div>
    </section>
  );
}
