import React from "react";
import { motion } from "framer-motion";
import { FaEnvelope, FaPhone } from "react-icons/fa";

export default function Portfolio() {
  return (
    <div className="flex flex-col lg:flex-row items-center justify-between">
      <div className="text-center lg:text-left">
        <motion.h1
          className="text-6xl font-bold mb-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <br /> Chiranjeevi Kumar...
        </motion.h1>
        <p className="text-xl italic text-gray-700 mb-6">
          Product Designer based in <span className="font-bold">Delhi</span>
        </p>
        <button className="bg-black text-white px-6 py-2 rounded-lg flex items-center gap-2">
          Let’s talk with me <span className="text-orange-400">↗</span>
        </button>
        <div className="mt-4 flex gap-6 items-center justify-center lg:justify-start">
          <div className="flex items-center gap-2">
            <FaPhone />
            <p className="font-semibold">+91 7870503085</p>
          </div>
          <div className="flex items-center gap-2">
            <FaEnvelope />
            <p className="font-semibold">chiranjeeviraj109@gmail.com</p>
          </div>
        </div>
      </div>

      {/* Profile Image */}
      <motion.div
        className="relative w-56 h-56 lg:w-80 lg:h-80"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <img src="your-image-path.jpg" alt="Jacob Jones" className="rounded-full shadow-lg z-10 relative" />
        <div className="absolute inset-0 bg-gradient-to-r from-purple-500 to-orange-500 opacity-50 rounded-full"></div>
      </motion.div>
    </div>
  );
}
