import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FaArrowRight, FaArrowLeft } from "react-icons/fa";

// Animation Variants
const fadeInUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
  exit: { opacity: 0, y: -30, transition: { duration: 0.4, ease: "easeIn" } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.25, delayChildren: 0.15 } },
};

// Project Data (unchanged)
const PROJECTS = [
  { id: 1, title: "EV Charging App", category: "Mobile App", image: "/your-image-path1.jpg", description: "A mobile solution for EV charging." },
  { id: 2, title: "Fintech Wallet", category: "Mobile App", image: "/your-image-path2.jpg", description: "Secure digital wallet solution." },
  { id: 3, title: "E-commerce App", category: "Mobile App", image: "/your-image-path3.jpg", description: "Online shopping platform." },
  { id: 4, title: "Bally Website Research", category: "UX Case Study", image: "/your-image-path4.jpg", description: "UX research project." },
];

const TESTIMONIALS = [
  {
    id: 1,
    quote: "File storage made easy – including powerful features you won’t find anywhere else.",
    author: "Larry Diamond",
    position: "CEO, Besnik",
    image: "/your-testimonial-image.jpg",
  },
  {
    id: 2,
    quote: "Great design work! It has helped improve our brand and user experience significantly.",
    author: "Jessica Smith",
    position: "Product Manager, TechCorp",
    image: "/your-testimonial-image2.jpg",
  },
];

// Project Card Component
const ProjectCard = ({ project }) => (
  <motion.div
    className="flex flex-col group bg-white rounded-3xl shadow-md overflow-hidden hover:shadow-2xl transition-all duration-300 border border-gray-100"
    variants={fadeInUp}
    whileHover={{ scale: 1.02, transition: { duration: 0.2 } }}
    whileFocus={{ scale: 1.02 }}
    tabIndex={0} // Accessibility improvement
  >
    <div className="relative overflow-hidden rounded-t-3xl">
      <img
        src={project.image}
        alt={project.title}
        className="w-full h-72 object-cover transition-transform duration-500 group-hover:scale-105"
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent opacity-70 group-hover:opacity-90 transition-opacity duration-300" />
      <p className="absolute bottom-4 left-4 text-white text-sm uppercase font-semibold tracking-wide">{project.category}</p>
    </div>
    <div className="p-6 text-center">
      <h3 className="text-2xl font-semibold text-gray-900 group-hover:text-teal-600 transition-colors duration-200">{project.title}</h3>
      <p className="text-gray-600 mt-3 text-base line-clamp-2">{project.description}</p>
    </div>
  </motion.div>
);

// Testimonial Component
const TestimonialCard = ({ testimonial, onPrev, onNext }) => (
  <motion.div
    className="relative p-8 md:p-12 rounded-3xl bg-gradient-to-br from-teal-500 via-green-600 to-teal-700 text-white shadow-xl max-w-3xl mx-auto"
    variants={fadeInUp}
  >
    <div className="flex flex-col md:flex-row items-center gap-8">
      <motion.img
        src={testimonial.image}
        alt={testimonial.author}
        className="w-20 h-20 md:w-28 md:h-28 rounded-full object-cover border-4 border-white/40 shadow-md"
        whileHover={{ scale: 1.05 }}
      />
      <div className="text-center md:text-left">
        <blockquote className="text-lg md:text-xl italic font-light tracking-wide leading-relaxed">“{testimonial.quote}”</blockquote>
        <p className="font-semibold mt-4 text-lg md:text-xl">{testimonial.author}</p>
        <p className="text-sm md:text-base opacity-75 mt-1">{testimonial.position}</p>
      </div>
    </div>
    <div className="absolute bottom-6 right-6 flex gap-4">
      <button
        onClick={onPrev}
        className="p-3 bg-white/20 hover:bg-white/40 rounded-full transition-all hover:scale-110 focus:outline-none focus:ring-2 focus:ring-white/50"
        aria-label="Previous testimonial"
      >
        <FaArrowLeft className="text-white" />
      </button>
      <button
        onClick={onNext}
        className="p-3 bg-white/20 hover:bg-white/40 rounded-full transition-all hover:scale-110 focus:outline-none focus:ring-2 focus:ring-white/50"
        aria-label="Next testimonial"
      >
        <FaArrowRight className="text-white" />
      </button>
    </div>
  </motion.div>
);

export default function Works() {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  useEffect(() => {
    if (isAutoPlaying) {
      const timer = setInterval(() => {
        setCurrentTestimonial((prev) => (prev + 1) % TESTIMONIALS.length);
      }, 6000); // Slightly longer interval for better readability
      return () => clearInterval(timer);
    }
  }, [isAutoPlaying]);

  const handleNextTestimonial = () => {
    setIsAutoPlaying(false);
    setCurrentTestimonial((prev) => (prev + 1) % TESTIMONIALS.length);
  };
  const handlePrevTestimonial = () => {
    setIsAutoPlaying(false);
    setCurrentTestimonial((prev) => (prev - 1 + TESTIMONIALS.length) % TESTIMONIALS.length);
  };

  return (
    <section className="py-24 px-6 md:px-12 lg:px-20 bg-gray-100 min-h-screen">
      <motion.header
        className="mb-20 text-center max-w-2xl mx-auto"
        variants={staggerContainer}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        <motion.span className="text-teal-600 text-sm uppercase tracking-widest font-semibold" variants={fadeInUp}>
          Our Work
        </motion.span>
        <motion.h1
          className="text-4xl md:text-5xl lg:text-6xl font-bold mt-4 text-gray-900 tracking-tight leading-tight"
          variants={fadeInUp}
        >
          Recent Projects
        </motion.h1>
      </motion.header>

      <motion.div
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10 max-w-7xl mx-auto"
        variants={staggerContainer}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        {PROJECTS.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </motion.div>

      <div className="mt-20">
        <AnimatePresence mode="wait">
          <motion.div key={currentTestimonial} initial="hidden" animate="visible" exit="exit" variants={fadeInUp}>
            <TestimonialCard
              testimonial={TESTIMONIALS[currentTestimonial]}
              onPrev={handlePrevTestimonial}
              onNext={handleNextTestimonial}
            />
          </motion.div>
        </AnimatePresence>
      </div>
    </section>
  );
}
