import React from "react";
import { motion } from "framer-motion";
import { FaEnvelope, FaPhone, FaMobileAlt, FaLaptopCode } from "react-icons/fa";
import myProfile from "../src/img/myProfile.JPG";

export default function Portfolio() {
  return (
    <div className="container mx-auto px-4 flex flex-col-reverse lg:flex-row items-center justify-between py-10">
      <div className="text-center lg:text-left w-full lg:w-1/2">
        <motion.h1
          className="text-4xl sm:text-5xl md:text-6xl font-bold mb-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          Chiranjeevi Kumar
        </motion.h1>
        <p className="text-lg sm:text-xl italic text-gray-700 mb-2">
          <span className="font-bold text-green-600">Mobile Developer</span> | React Native | Swift | Kotlin
        </p>
        <p className="text-base sm:text-lg text-gray-600 mb-6">
          Passionate about crafting seamless and high-performance mobile applications. Experienced in **iOS & Android development**, API integrations, and UI/UX optimization.
        </p>

        <button className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg flex items-center gap-2 mx-auto lg:mx-0 transition-all">
          Letâ€™s Collaborate <span className="text-orange-400">ðŸš€</span>
        </button>

        <div className="mt-6 flex flex-col sm:flex-row gap-4 sm:gap-6 items-center justify-center lg:justify-start">
          <div className="flex items-center gap-2">
            <FaPhone className="text-green-600" />
            <p className="font-semibold text-sm sm:text-base">+91 7870503085</p>
          </div>
          <div className="flex items-center gap-2">
            <FaEnvelope className="text-green-600" />
            <p className="font-semibold text-sm sm:text-base">chiranjeeviraj109@gmail.com</p>
          </div>
        </div>
      </div>

      {/* Profile Image */}
      <motion.div
        className="relative w-40 h-40 sm:w-56 sm:h-56 lg:w-72 lg:h-72 mb-6 lg:mb-0"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <img src={myProfile} alt="Profile" className="rounded-full shadow-lg z-10 relative w-full h-full object-cover" />
        <div className="absolute inset-0 bg-gradient-to-r from-green-500 to-blue-500 opacity-50 rounded-full"></div>
      </motion.div>
    </div>
  );
}
