import React from "react";
import { BrowserRouter as Router, Routes, Route, NavLink } from "react-router-dom";
import { FaBehance, FaDribbble, FaGithub, FaInstagram, FaLinkedin } from "react-icons/fa";
import Portfolio from "./Portfolio";
import About from "./About";
import Services from "./Services";
import Works from "./Works";
import Blog from "./Blog";
import Contact from "./Contact";

const navigationItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Services", path: "/services" },
  { name: "Works", path: "/works" },
  { name: "Blog", path: "/blog" },
  { name: "Contact", path: "/contact" },
];

const socialLinks = [
  { icon: <FaLinkedin size={24} />, href: "https://behance.net/your-profile" },
  { icon: <FaGithub size={24} />, href: "https://dribbble.com/your-profile" },
  { icon: <FaInstagram size={24} />, href: "https://instagram.com/your-profile" },
];

export default function App() {
  return (
    <Router>
      <div className="flex min-h-screen bg-gray-50 text-gray-900">
        {/* Sidebar */}
        <aside className="fixed top-0 left-0 w-64 h-full p-6 bg-gray-900 text-white flex flex-col justify-between shadow-lg">
          <div>
            <h1 className="text-2xl font-semibold tracking-tight">Chiranjeevi</h1>
            <nav className="mt-10 space-y-4">
              {navigationItems.map((item) => (
                <NavLink
                  key={item.name}
                  to={item.path}
                  exact={item.path === "/"}
                  className={({ isActive }) =>
                    `block text-gray-400 hover:text-white transition-colors duration-200 ${
                      isActive ? "text-white font-medium" : ""
                    }`
                  }
                >
                  {item.name}
                </NavLink>
              ))}
            </nav>
          </div>
          <div className="space-y-6">
            <div className="flex gap-6">
              {socialLinks.map((link, index) => (
                <a
                  key={index}
                  href={link.href}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-gray-400 hover:text-white transition-colors duration-200"
                >
                  {link.icon}
                </a>
              ))}
            </div>
            <p className="text-gray-500 text-xs">
              © {new Date().getFullYear()} Chiranjeevi Kumar. All rights reserved.
            </p>
          </div>
        </aside>

        {/* Main Content */}
        <main className="flex-1 ml-64 p-8 md:p-12 lg:p-16 overflow-y-auto">
          <Routes>
            <Route path="/" element={<Portfolio />} />
            <Route path="/about" element={<About />} />
            <Route path="/services" element={<Services />} />
            <Route path="/works" element={<Works />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}
