import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPlus, FaMinus } from "react-icons/fa";

// Service Data with Your Details
const services = [
  {
    title: "Mobile App Development",
    description:
      "I craft high-performance, cross-platform mobile applications using React Native, Swift, and Kotlin, tailored to client needs.",
    image: null, // Replace with "/path/to/mobile-app-image.jpg" if you have one
  },
  {
    title: "API Integration",
    description:
      "Seamlessly integrate REST APIs and GraphQL into mobile apps to ensure robust data flow and functionality.",
    image: null,
  },
  {
    title: "UI/UX Optimization",
    description:
      "Enhance user experiences by designing intuitive interfaces and optimizing app performance for iOS and Android.",
    image: null,
  },
  {
    title: "Freelance Consulting",
    description:
      "As a freelance mobile developer, I offer end-to-end solutions, from prototyping to deployment, for startups and businesses.",
    image: null,
  },
];

// Animation Variants
const cardVariants = {
  hidden: { opacity: 0, height: 0 },
  visible: { opacity: 1, height: "auto", transition: { duration: 0.4, ease: "easeOut" } },
};

export default function Services() {
  const [expanded, setExpanded] = useState(null);

  const toggleExpand = (index) => {
    setExpanded(expanded === index ? null : index);
  };

  return (
    <section className="py-12 md:py-16 px-4 sm:px-6 lg:px-8 max-w-5xl mx-auto">
      <div className="text-center md:text-left mb-10">
        <h2 className="text-lg text-gray-500 italic">Services</h2>
        <h1 className="text-4xl md:text-5xl font-bold text-gray-900">
          My Specialties
        </h1>
      </div>

      <div className="space-y-4">
        {services.map((service, index) => (
          <div
            key={index}
            className="border-b border-gray-200 py-4 last:border-b-0"
          >
            <div
              className="flex justify-between items-center cursor-pointer group"
              onClick={() => toggleExpand(index)}
            >
              <h3
                className={`text-xl md:text-2xl font-semibold text-gray-800 group-hover:text-green-600 transition-colors duration-200 ${
                  expanded === index ? "text-green-600" : ""
                }`}
              >
                {service.title}
              </h3>
              <span className="text-gray-600 group-hover:text-green-600 transition-colors duration-200">
                {expanded === index ? <FaMinus /> : <FaPlus />}
              </span>
            </div>
            {expanded === index && (
              <motion.div
                className="mt-4 text-gray-600 flex flex-col md:flex-row gap-6"
                variants={cardVariants}
                initial="hidden"
                animate="visible"
              >
                {service.image && (
                  <img
                    src={service.image}
                    alt={`${service.title} example`}
                    className="w-32 md:w-40 rounded-md shadow-md object-cover"
                  />
                )}
                <p className="text-base md:text-lg max-w-2xl">
                  {service.description}
                </p>
              </motion.div>
            )}
          </div>
        ))}
      </div>
    </section>
  );
}
