import React from "react";
import { motion } from "framer-motion";
import { FaEnvelope, FaPhone, FaMapMarkerAlt } from "react-icons/fa";

// Animation Variants
const fadeIn = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
};

// Data
const contactData = [
  {
    icon: <FaPhone className="text-green-700 text-xl" />,
    text: "+91 7870503085",
    href: "tel:+917870503085",
  },
  { icon: <FaMapMarkerAlt className="text-green-700 text-xl" />, text: "India" },
  {
    icon: <FaEnvelope className="text-green-700 text-xl" />,
    text: "chiranjeeviraj109@gmail.com",
    href: "mailto:chiranjeeviraj109@gmail.com",
  },
];

const experienceData = [
  {
    year: "2022 - Present",
    role: "Mobile Developer",
    company: "Tech Innovate",
    description: "Led development of cross-platform apps using React Native and integrated REST APIs.",
  },
  {
    year: "2021 - 2022",
    role: "Junior Mobile Developer",
    company: "AppWorks",
    description: "Built native iOS and Android apps with Swift and Kotlin, focusing on UI/UX.",
  },
  {
    year: "2020 - 2021",
    role: "Software Intern",
    company: "CodeCraft",
    description: "Assisted in developing mobile prototypes and Firebase integrations.",
  },
];

// Reusable Contact Item Component
const ContactItem = React.memo(({ icon, text, href }) => (
  <motion.div
    className="flex items-center gap-4 p-4 bg-white rounded-lg shadow-sm hover:shadow-md transition-all duration-300"
    variants={fadeIn}
    whileHover={{ y: -3 }}
  >
    {icon}
    {href ? (
      <a
        href={href}
        className="text-gray-700 text-base font-medium hover:text-green-700 transition-colors duration-200"
      >
        {text}
      </a>
    ) : (
      <span className="text-gray-700 text-base font-medium">{text}</span>
    )}
  </motion.div>
));

// Reusable Experience Card Component
const ExperienceCard = React.memo(({ year, role, company, description }) => (
  <motion.div
    className="p-6 bg-white rounded-lg shadow-md hover:shadow-xl transition-all duration-300 border border-gray-100"
    variants={fadeIn}
    whileHover={{ y: -5 }}
  >
    <p className="text-sm text-gray-500 font-medium">{year}</p>
    <h4 className="text-xl font-bold text-gray-900 mt-1 tracking-tight">{role}</h4>
    <p className="text-green-700 font-semibold">{company}</p>
    <p className="text-gray-600 text-sm mt-2 leading-relaxed">{description}</p>
  </motion.div>
));

export default function About() {
  return (
    <section
      className="py-20 px-4 sm:px-6 lg:px-8 max-w-6xl mx-auto bg-gray-50"
      aria-label="About Me Section"
    >
      <div className="bg-white rounded-2xl shadow-lg p-8 md:p-12 space-y-16">
        {/* Header */}
        <motion.div
          className="text-center md:text-left"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <motion.h1
            className="text-4xl md:text-5xl font-extrabold text-gray-900 tracking-tight"
            variants={fadeIn}
          >
            About Me
          </motion.h1>
          <motion.h2
            className="mt-4 text-2xl md:text-3xl font-semibold text-green-700"
            variants={fadeIn}
          >
            Chiranjeevi Kumar
          </motion.h2>
          <motion.p
            className="mt-4 text-lg md:text-xl text-gray-600 italic max-w-3xl mx-auto md:mx-0 leading-relaxed"
            variants={fadeIn}
          >
            Mobile Developer based in <span className="font-semibold text-gray-800">India</span>
          </motion.p>
        </motion.div>

        {/* Contact Info */}
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-3 gap-6"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
          role="list"
        >
          {contactData.map((item, index) => (
            <ContactItem
              key={index}
              icon={item.icon}
              text={item.text}
              href={item.href}
            />
          ))}
        </motion.div>

        {/* Experience Section */}
        <motion.div
          className="space-y-8"
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          <motion.h3
            className="text-2xl font-bold text-gray-800"
            variants={fadeIn}
          >
            Experience
          </motion.h3>
          <motion.p
            className="text-lg text-gray-600 max-w-3xl leading-relaxed"
            variants={fadeIn}
          >
            Hi! Iâ€™m Chiranjeevi Kumar, a passionate mobile developer with 3 years of experience crafting seamless applications for iOS and Android.
          </motion.p>
          <motion.a
            href="/resume.pdf"
            download="Chiranjeevi_Kumar_Resume.pdf"
            className="inline-block px-8 py-3 bg-gradient-to-r from-green-600 to-green-700 text-white font-semibold rounded-full hover:from-green-700 hover:to-green-800 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 shadow-lg"
            variants={fadeIn}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            aria-label="Download Resume"
          >
            Download Resume
          </motion.a>

          <div className="space-y-6">
            {experienceData.map((exp) => (
              <ExperienceCard
                key={exp.role}
                year={exp.year}
                role={exp.role}
                company={exp.company}
                description={exp.description}
              />
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
}
