import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

// Animation Variants
const fadeIn = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.2 } },
};

// Simulated JSON data (replace with fetch from external file/API)
const blogPostsJSON = [
  {
    id: 1,
    image: "https://source.unsplash.com/400x300/?mobile,development",
    date: "15 January, 2025",
    title: "Building Cross-Platform Apps with React Native",
    excerpt: "Exploring the power of React Native for seamless iOS and Android development.",
  },
  {
    id: 2,
    image: "https://source.unsplash.com/400x300/?api,code",
    date: "20 January, 2025",
    title: "Mastering API Integration in Mobile Apps",
    excerpt: "Tips and tricks for integrating REST and GraphQL APIs effectively.",
  },
  {
    id: 3,
    image: "https://source.unsplash.com/400x300/?ui,design",
    date: "25 January, 2025",
    title: "UI/UX Best Practices for Mobile Developers",
    excerpt: "How to design intuitive interfaces that enhance user experience.",
  },
];

// Simulated fetch function (replace with actual fetch in production)
const fetchBlogPosts = () => {
  return new Promise((resolve) => {
    setTimeout(() => resolve(blogPostsJSON), 500); // Simulate network delay
  });
};

// Reusable Blog Card Component
const BlogCard = React.memo(({ id, image, date, title, excerpt }) => (
  <motion.article
    className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden border border-gray-100"
    variants={fadeIn}
    whileHover={{ y: -5 }}
  >
    <img src={image} alt={title} className="w-full h-52 object-cover" loading="lazy" />
    <div className="p-6">
      <p className="text-sm text-gray-500 font-medium italic">{date}</p>
      <h3 className="mt-2 text-xl font-bold text-gray-900 hover:text-green-700 transition-colors duration-200">
        {title}
      </h3>
      <p className="mt-2 text-gray-600 text-base line-clamp-2 leading-relaxed">
        {excerpt}
      </p>
      <a
        href={`/blog/${id}`} // Assuming a routing setup like React Router
        className="mt-4 inline-block text-green-700 font-semibold hover:text-green-800 transition-colors duration-200"
        aria-label={`Read more about ${title}`}
      >
        Read More →
      </a>
    </div>
  </motion.article>
));

export default function Blog() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch blog posts on mount
  useEffect(() => {
    fetchBlogPosts()
      .then((data) => {
        setPosts(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching blog posts:", error);
        setError("Failed to load blog posts. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <section
      className="py-16 md:py-20 px-4 sm:px-6 lg:px-8 max-w-6xl mx-auto bg-gray-50"
      aria-label="Blog Section"
    >
      {/* Blog Header */}
      <motion.div
        className="text-center mb-16"
        variants={staggerContainer}
        initial="hidden"
        animate="visible"
      >
        <motion.h2
          className="text-lg text-gray-500 font-medium italic"
          variants={fadeIn}
        >
          Blog
        </motion.h2>
        <motion.h1
          className="text-4xl md:text-5xl font-extrabold text-gray-900 mt-2 tracking-tight"
          variants={fadeIn}
        >
          Insights from Chiranjeevi
        </motion.h1>
        <motion.p
          className="mt-4 text-lg md:text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed"
          variants={fadeIn}
        >
          Sharing my journey and expertise as a freelance mobile developer.
        </motion.p>
      </motion.div>

      {/* Loading, Error, or Blog Grid */}
      {loading ? (
        <div className="text-center text-gray-600 text-lg">Loading posts...</div>
      ) : error ? (
        <div className="text-center text-red-600 text-lg">{error}</div>
      ) : (
        <>
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
            variants={staggerContainer}
            initial="hidden"
            animate="visible"
            role="list"
          >
            {posts.map((post) => (
              <BlogCard
                key={post.id}
                id={post.id}
                image={post.image}
                date={post.date}
                title={post.title}
                excerpt={post.excerpt}
              />
            ))}
          </motion.div>

          {/* Load More Button */}
          <div className="flex justify-center mt-12">
            <motion.button
              className="px-8 py-3 bg-gradient-to-r from-green-600 to-green-700 text-white font-semibold rounded-full hover:from-green-700 hover:to-green-800 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 shadow-lg"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Load More
              <span className="ml-2" role="img" aria-label="arrow">
                ↗️
              </span>
            </motion.button>
          </div>
        </>
      )}
    </section>
  );
}
