import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaPaperclip, FaPhone, FaEnvelope, FaMapMarkerAlt, FaLinkedin, FaGithub } from "react-icons/fa";
import emailjs from "@emailjs/browser";

const contactInfo = {
  phone: "+91 7870503085",
  email: "chiranjeeviraj109@gmail.com",
  location: "India",
  socials: [
    { name: "LinkedIn", href: "https://linkedin.com/in/imchiranjeevi", icon: <FaLinkedin size={24} /> },
    { name: "GitHub", href: "https://github.com/Chiranjeeviprogrammer", icon: <FaGithub size={24} /> },
  ],
};

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    file: null,
  });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus("sending");

    const templateParams = {
      from_name: formData.name,
      from_email: formData.email,
      message: formData.message,
      reply_to: formData.email,
    };

    emailjs
      .send("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", templateParams, "YOUR_PUBLIC_KEY")
      .then(
        () => {
          setStatus("success");
          setFormData({ name: "", email: "", message: "", file: null });
          setTimeout(() => setStatus(""), 3000);
        },
        (error) => {
          console.error("EmailJS Error:", error);
          setStatus("error");
          setTimeout(() => setStatus(""), 3000);
        }
      );
  };

  return (
    <section className="min-h-screen bg-gray-50 flex items-center justify-center px-4 py-16">
      <div className="max-w-4xl w-full bg-white shadow-xl rounded-2xl p-6 md:p-10 grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Contact Info */}
        <div className="bg-gradient-to-br from-green-600 to-green-800 text-white p-6 md:p-10 rounded-xl flex flex-col justify-between">
          <div>
            <h1 className="text-3xl md:text-4xl font-bold">Letâ€™s Connect</h1>
            <p className="text-gray-200 mt-3">Have a project in mind or just want to chat? Reach out!</p>
            <div className="mt-6 space-y-4">
              {[contactInfo.location, contactInfo.phone, contactInfo.email].map((item, index) => (
                <div key={index} className="flex items-center gap-3">
                  {index === 0 ? <FaMapMarkerAlt size={22} /> : index === 1 ? <FaPhone size={22} /> : <FaEnvelope size={22} />}
                  <a href={index === 1 ? `tel:${item}` : index === 2 ? `mailto:${item}` : undefined} className="hover:underline">
                    {item}
                  </a>
                </div>
              ))}
            </div>
          </div>
          <div className="mt-6 flex space-x-4">
            {contactInfo.socials.map((social, index) => (
              <a key={index} href={social.href} target="_blank" rel="noopener noreferrer" className="hover:scale-110 transition-transform">
                {social.icon}
              </a>
            ))}
          </div>
        </div>

        {/* Contact Form */}
        <div className="p-6 md:p-10">
          <h2 className="text-2xl md:text-3xl font-semibold text-gray-900">Drop Me a Line</h2>
          <form onSubmit={handleSubmit} className="mt-6 space-y-4">
            {["name", "email"].map((field, index) => (
              <input
                key={index}
                type={field === "email" ? "email" : "text"}
                name={field}
                placeholder={`Your ${field.charAt(0).toUpperCase() + field.slice(1)}`}
                value={formData[field]}
                onChange={handleChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 outline-none"
                required
              />
            ))}
            <textarea
              name="message"
              placeholder="Your Message"
              rows="4"
              value={formData.message}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 outline-none"
              required
            />
            <label className="flex items-center gap-3 cursor-pointer">
              <FaPaperclip size={20} />
              <span className="text-sm">{formData.file ? `Attached: ${formData.file.name}` : "Attach a File (optional)"}</span>
              <input type="file" name="file" onChange={handleChange} className="hidden" />
            </label>
            <button
              type="submit"
              disabled={status === "sending"}
              className={`w-full p-3 rounded-lg font-semibold text-white transition-all ${
                status === "sending" ? "bg-gray-400 cursor-not-allowed" : "bg-green-600 hover:bg-green-700"
              }`}
            >
              {status === "sending" ? "Sending..." : "Send Message"}
            </button>
            {status === "success" && <p className="mt-3 text-green-600">Message sent successfully!</p>}
            {status === "error" && <p className="mt-3 text-red-600">Failed to send message. Please try again.</p>}
          </form>
        </div>
      </div>
    </section>
  );
}
